requestAll:
{
  authors {
    id
    firstName
   books {id chapters {number}} pens {id} }
}

requestAllInlineFragment:
{
  authors {
    firstName
    id
 pens {id}   books {
      ... on Book {
        id
        title
        chapters {
          ... on Chapter {
            number
          }
        }
      }
    }
  }
}

requestAllFragment:
{
  authors {
    firstName
    id
 pens {id}   books {
      ...booksFrag
    }
  }
}
fragment booksFrag on Book {
  id
  title
  chapters {
    ...chaptersFrag
  }
}
fragment chaptersFrag on Chapter {
  id
  number
}
